
public class Customer {
private int id;
private String name;
private int tier;
public Customer() {
	// TODO Auto-generated constructor stub
}
public int getId() {
	return id;
}
public void setId(int id) {
	this.id = id;
}
public String getName() {
	return name;
}
public void setName(String name) {
	this.name = name;
}
public int getTier() {
	return tier;
}
public void setTier(int tier) {
	this.tier = tier;
}

}

